mapper.insert:

insert into t_user_info (id, USER_NAME_, PASS_WORD_, 
  USERE_EMAIL_, USER_MOBILE_, SEX_, 
  SYS_ROLE_ID_, USER_STATUS_, REGISTER_TIME_, 
  LAST_LOGIN_IP_, LAST_LOGIN_TIME_, DELTE_FLAG_
  )
values (#{id,jdbcType=BIGINT}, #{userName,jdbcType=VARCHAR}, #{passWord,jdbcType=VARCHAR}, 
  #{usereEmail,jdbcType=VARCHAR}, #{userMobile,jdbcType=INTEGER}, #{sex,jdbcType=INTEGER}, 
  #{sysRoleId,jdbcType=INTEGER}, #{userStatus,jdbcType=INTEGER}, #{registerTime,jdbcType=VARCHAR}, 
  #{lastLoginIp,jdbcType=VARCHAR}, #{lastLoginTime,jdbcType=VARCHAR}, #{delteFlag,jdbcType=INTEGER}
  )

mapper.select:
select id, USER_NAME_, PASS_WORD_, USERE_EMAIL_, USER_MOBILE_, SEX_, SYS_ROLE_ID_,
USER_STATUS_, REGISTER_TIME_, LAST_LOGIN_IP_, LAST_LOGIN_TIME_, DELTE_FLAG_
from t_user_info where DELTE_FLAG_ = 0 and USER_NAME_ = #{userName,jdbcType=VARCHAR}
